var SDL = require('sdl');

module.exports = function (width, height) {

  SDL.init(SDL.INIT.VIDEO);

  var screen = SDL.setVideoMode(width, height, 0, 0);

  SDL.events.on('QUIT', function () { process.exit(0); });

  var platform = {
    type: "nodeSDL",
    loadTexture: undefined,
    setTitle: setTitle,
    setIcon: setIcon,
    width: screen.w,
    height: screen.h,
    gl: undefined
  };

  return platform;

  function setTitle(title) {
    SDL.WM.setCaption(title);
  }

  function setIcon(path) {
    SDL.WM.setIcon(path);
  }

};

